data StringPrepProfile = Profile
    {
        maps :: [Map],
        shouldNormalize :: Bool,
        prohibited :: [Prohibited],
        shouldCheckBidi :: Bool,
        handleInsigchar :: Bool
    }

runStringPrep :: StringPrepProfile -> ByteString -> Maybe Text
runStringPrep (Profile maps norm prohibs bidi handleinsig) s = result
    where
        transcoded = TEnc.decodeUtf8With (TEncerr.lenientDecode) s
        prohibited = toSet $ concat prohibs
        mapped = foldr Text.concatMap transcoded maps
        normed = if norm
            then normalize NFKC mapped
            else mapped
        bidid = if bidi
                then if checkBidi normed
                    then True
                    else False
                else True
        handleinsiged = if bidid
                then if handleinsig
                    then Just (processInsigchar normed)
                    else Just normed
                else Nothing
        result = case handleinsiged of
            Nothing -> Nothing
            Just t -> if Text.any (\x -> member x prohibited) t
                then Nothing
                else Just t

checkBidi :: Text -> Bool
checkBidi t = not containsRandL || not containsAL && firstRandL && lastRandL
    where
        containsRandL = Text.any (\x -> member x randl) t
        containsAL = Text.any (\x -> member  x l) t
        firstRandL = member (Text.head t) randl
        lastRandL = member (Text.last t) randl

processInsigchar :: Text -> Text
processInsigchar t = t1
    where
        t1 = if Text.null (Text.strip(t)) then Text.pack ("  ") 
            else Text.append (Text.append t2 t3) t2
                where
                    t2 = Text.pack " " 
                    t3 = Text.pack(Textregex.subRegex (mkRegex "\x20+")  (Text.unpack(Text.strip(t))) "  ")

